document.addEventListener("DOMContentLoaded", () => {
    document.querySelector(".btn.btn-primary").addEventListener("click", event => {
        var itemList = document.getElementById("my-list");
        var template = document.getElementById("list-template");

        // Lista de personajes de Rick and Morty con campos adicionales
        var characters = [
            { 
                name: "Davin",
                description: "Dead - Human",
                image: "data:image/jpeg;base64,", // URL de la imagen de Davin
                lastKnownLocation: "Earth (C-137)",
                firstSeenIn: "Pilot"
            },
            { 
                name: "Amy Poopybutthole",
                description: "Alive - Poopybutthole",
                image: "data:image/jpeg;base64,", // URL de la imagen de Amy (debes proporcionar tu propia URL)
                lastKnownLocation: "unknown",
                firstSeenIn: "The Rickchurian Mortydate"
            },
            { 
                name: "Slow Mobius",
                description: "Alive - Humanoid",
                image: "data:image/jpeg;base64,", // URL de la imagen de Slow Mobius (debes proporcionar tu propia URL)
                lastKnownLocation: "Earth (Replacement Dimension)",
                firstSeenIn: "Ricksy Business"
            },
            // ... (otros personajes)
        ];

        // Seleccionar un personaje aleatorio
        var randomCharacter = characters[Math.floor(Math.random() * characters.length)];

        // Calcular el número total de elementos en la lista
        var total = itemList.childElementCount + 1;

        // Clonar la plantilla
        var clone = template.content.cloneNode(true);

        // Actualizar los datos en el clon con el personaje aleatorio y campos adicionales
        clone.querySelector("[data-id='number']").textContent = `${total}`;
        clone.querySelector("[data-id='title']").textContent = randomCharacter.name;

        // Agregar la imagen al lado izquierdo
        var imageElement = document.createElement("img");
        imageElement.src = randomCharacter.image;
        imageElement.alt = randomCharacter.name;
        clone.querySelector("[data-id='content']").appendChild(imageElement);

        // Agregar la información del personaje al lado derecho
        clone.querySelector("[data-id='content']").innerHTML += `
            <span data-id="description">${randomCharacter.description}</span>
            <br> <!-- Salto de línea resaltado -->
            <span data-id="last-known-location">${randomCharacter.lastKnownLocation}</span>
            <span data-id="first-seen-in">${randomCharacter.firstSeenIn}</span>
        `;

        // Agregar el clon a la lista
        itemList.appendChild(clone);
    });

    document.querySelector(".btn.btn-light").addEventListener("click", event => {
        var itemList = document.getElementById("my-list");
        itemList.replaceChildren();
    });
});
